import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms'; // ✅ CORRECT
import { AuthService } from '../../core/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-prof-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './prof-login.component.html',
  styleUrls: ['./prof-login.component.scss']
})
export class ProfLoginComponent {
  email = '';
  password = '';
  loading = false;
  error = '';

  constructor(private auth: AuthService, private router: Router) {}

  onLogin() {
    this.loading = true;
    this.error = '';
    this.auth.login(this.email, this.password).subscribe({
      next: (res: any) => {
        this.auth.saveToken(res.token);
        this.router.navigate(['/dashboard']);
      },
      error: () => { this.error = 'Email ou mot de passe invalide'; },
      complete: () => this.loading = false
    });
  }
}
<div class="row justify-content-center">
  <div class="col-12 col-md-6 col-lg-4">
    <div class="card p-4">
      <h2 class="mb-3">Connexion Professeur</h2>
      <form (ngSubmit)="onLogin()">
        <div class="mb-3">
          <label class="form-label">Email</label>
          <input type="email" class="form-control" [(ngModel)]="email" name="email" required>
        </div>
        <div class="mb-3">
          <label class="form-label">Mot de passe</label>
          <input type="password" class="form-control" [(ngModel)]="password" name="password" required>
        </div>
        <button class="btn btn-primary w-100" [disabled]="loading">
          {{ loading ? 'Connexion...' : 'Se connecter' }}
        </button>
        <div class="text-danger mt-2" *ngIf="error">{{ error }}</div>
      </form>
    </div>
  </div>
</div>
