import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AuthService } from '../../core/services/auth.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-prof-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './prof-login.component.html',
  styleUrls: ['./prof-login.component.scss']
})
export class ProfLoginComponent {
  email = '';
  password = '';
  loading = false;
  error = '';

  constructor(private auth: AuthService, private router: Router) {}

  onLogin() {
    this.loading = true;
    this.error = '';
    this.auth.login(this.email, this.password).subscribe({
      next: (res: any) => {
        this.auth.saveToken(res.token);
        this.router.navigate(['/dashboard']);
      },
      error: () => { this.error = 'Email ou mot de passe invalide'; },
      complete: () => this.loading = false
    });
  }
}
<div class="login-container">
  <div class="login-card">
    <div class="login-header">
      <div class="icon-wrapper">
        <span class="login-icon">🎓</span>
      </div>
      <h2 class="login-title">Connexion Professeur</h2>
      <p class="login-subtitle">Accédez à votre espace personnel</p>
    </div>

    <form (ngSubmit)="onLogin()" class="login-form">
      <div class="form-group">
        <label class="form-label">Adresse email</label>
        <input 
          type="email" 
          class="form-control" 
          [(ngModel)]="email" 
          name="email" 
          placeholder="professeur@example.com"
          required
        >
      </div>

      <div class="form-group">
        <label class="form-label">Mot de passe</label>
        <input 
          type="password" 
          class="form-control" 
          [(ngModel)]="password" 
          name="password" 
          placeholder="••••••••"
          required
        >
      </div>

      <button class="btn btn-primary w-100 login-btn" [disabled]="loading">
        <span *ngIf="!loading">Se connecter</span>
        <span *ngIf="loading">
          <span class="spinner-border spinner-border-sm me-2"></span>
          Connexion en cours...
        </span>
      </button>

      <div class="alert alert-danger mt-3" *ngIf="error">
        {{ error }}
      </div>
    </form>
  </div>
</div>
