import { Component, OnInit } from '@angular/core';
import { Router, RouterLink } from '@angular/router';
import { CommonModule } from '@angular/common';
import { AuthService } from '../../core/services/auth.service';
import { ProfService } from '../../core/services/prof.service';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent implements OnInit {
  userName: string = '';

  constructor(
    public auth: AuthService,
    private profService: ProfService,
    private router: Router
  ) {}

  ngOnInit() {
    // Si le prof est connecté → on récupère ses infos depuis l’API
    if (this.auth.isLoggedIn()) {
      this.profService.me().subscribe({
        next: (prof: any) => {
          this.userName = prof.name || '';
        },
        error: (err) => {
          console.error('Erreur lors du chargement du profil :', err);
        }
      });
    }
  }

  gotoDashboard() {
    this.router.navigate(['/dashboard']);
  }

  logout() {
    this.auth.logout();
  }
}
<nav class="navbar navbar-expand-lg app-navbar shadow-sm">
  <div class="container">
    <!-- Logo -->
    <a class="navbar-brand d-flex align-items-center" routerLink="/">
      <span class="brand-icon me-2">📘</span>
      <span class="brand-text">EduPlatform</span>
    </a>

    <!-- Toggle mobile -->
    <button
      class="navbar-toggler"
      type="button"
      data-bs-toggle="collapse"
      data-bs-target="#mainNav"
    >
      <span class="navbar-toggler-icon"></span>
    </button>

    <!-- Menu -->
    <div id="mainNav" class="collapse navbar-collapse justify-content-end">
      <ul class="navbar-nav align-items-center gap-3">

        <!-- Si connecté -->
        <ng-container *ngIf="auth.isLoggedIn(); else loggedOut">
          <li class="nav-item">
            <div class="user-info d-flex align-items-center">
              <div class="user-avatar me-2">👤</div>
              <span class="user-name">{{ userName }}</span>
            </div>
          </li>

          <li class="nav-item">
            <button class="btn btn-primary btn-sm" (click)="gotoDashboard()">Dashboard</button>
          </li>

          <li class="nav-item">
            <button class="btn btn-link text-danger btn-sm" (click)="logout()">Déconnexion</button>
          </li>
        </ng-container>

        <!-- Si NON connecté -->
        <ng-template #loggedOut>
          <li class="nav-item">
            <a class="btn btn-outline-primary btn-sm" routerLink="/login">Connexion</a>
          </li>
        </ng-template>
      </ul>
    </div>
  </div>
</nav>
