import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Tu peux ajouter ici des variables dynamiques si besoin
  appName = 'EduPlatform';

  constructor() {}

  // Exemple (facultatif) : fonction pour scroller vers le bas
  scrollToContent() {
    window.scrollTo({ top: 800, behavior: 'smooth' });
  }
}
<section class="hero">
  <div class="container text-center py-5">
    <div class="row align-items-center">
      
      <!-- 🧾 Texte de présentation -->
      <div class="col-md-6 text-md-start text-center mb-4 mb-md-0">
        <h1 class="fw-bold mb-3 title">
          Bienvenue sur <span class="brand">EduPlatform</span>
        </h1>
        <p class="lead mb-4">
          Une plateforme intuitive permettant aux <strong>professeurs</strong> 
          de partager facilement leurs <strong>cours</strong>, <strong>TD</strong> et <strong>TP</strong> 
          avec leurs étudiants.
        </p>
        <div>
          <a routerLink="/login" class="btn btn-primary btn-lg me-2">Espace Professeur</a>
<a routerLink="/student" class="btn btn-outline-primary btn-lg">
  Espace Étudiant
</a>
        </div>
      </div>

      <!-- 🖼️ Image de présentation -->
      <div class="col-md-6 text-center">
        <img 
          src="https://cdn-icons-png.flaticon.com/512/2920/2920320.png" 
          alt="Illustration EduPlatform"
          class="img-fluid hero-image"
        />
      </div>

    </div>
  </div>
</section>
