import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { FileService } from '../../core/services/file.service';
import { AuthService } from '../../core/services/auth.service';

@Component({
  selector: 'app-prof-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './prof-dashboard.component.html',
  styleUrls: ['./prof-dashboard.component.scss']
})
export class ProfDashboardComponent implements OnInit {
  files: any[] = [];
  title = '';
  type = 0;
  selectedFile: File | null = null;
  slug = '';

  uploading = false;
  deletingId: number | null = null;

  constructor(private fileService: FileService, private auth: AuthService) {}
getTypeLabel(type: number): string {
  switch (type) {
    case 0: return 'Cours';
    case 1: return 'TD';
    case 2: return 'TP';
    case 3: return 'Autre';
    default: return 'Inconnu';
  }
}

  ngOnInit() {
    this.slug = this.auth.getProfSlugFromToken();
    this.loadFiles();
  }

  onFileSelected(e: any) { this.selectedFile = e.target.files[0]; }

  onUpload() {
    if (!this.selectedFile || !this.title) return;
    this.uploading = true;
    this.fileService.uploadFile(this.title, this.type, this.selectedFile).subscribe({
      next: () => { this.title=''; this.type=0; this.selectedFile=null; this.loadFiles(); },
      complete: () => this.uploading = false
    });
  }

  loadFiles() {
    if (!this.slug) return;
    this.fileService.getFilesByProf(this.slug).subscribe(res => this.files = res);
  }

  delete(id: number) {
    this.deletingId = id;
    this.fileService.deleteFile(id).subscribe({
      next: () => this.loadFiles(),
      complete: () => this.deletingId = null
    });
  }
}
<div class="dashboard-container">
  <div class="dashboard-header">
    <div>
      <h1 class="dashboard-title">Tableau de bord</h1>
      <p class="dashboard-subtitle">Gérez vos ressources pédagogiques</p>
    </div>
    <button class="btn btn-outline-primary" (click)="loadFiles()">
      <span class="me-2">🔄</span>
      Actualiser
    </button>
  </div>

  <div class="row g-4">
     Upload Section 
    <div class="col-12 col-lg-5">
      <div class="card upload-card">
        <div class="card-header">
          <h3 class="card-title">
            <span class="me-2">📤</span>
            Ajouter une ressource
          </h3>
        </div>

        <form (ngSubmit)="onUpload()" class="upload-form">
          <div class="form-group">
            <label class="form-label">Titre de la ressource</label>
            <input 
              class="form-control" 
              [(ngModel)]="title" 
              name="title" 
              placeholder="Ex: Cours de mathématiques"
              required
            >
          </div>

          <div class="form-group">
            <label class="form-label">Type de ressource</label>
            <select class="form-select" [(ngModel)]="type" name="type">
              <option [value]="0">📖 Cours</option>
              <option [value]="1">📝 TD</option>
              <option [value]="2">🔬 TP</option>
              <option [value]="3">📁 Autre</option>
            </select>
          </div>

          <div class="form-group">
            <label class="form-label">Fichier</label>
            <input 
              type="file" 
              class="form-control" 
              (change)="onFileSelected($event)"
            >
            <small class="form-text">PDF, DOCX, PPTX acceptés</small>
          </div>

          <button class="btn btn-primary w-100 upload-btn" [disabled]="uploading">
            <span *ngIf="!uploading">
              <span class="me-2">⬆️</span>
              Uploader la ressource
            </span>
            <span *ngIf="uploading">
              <span class="spinner-border spinner-border-sm me-2"></span>
              Upload en cours...
            </span>
          </button>
        </form>
      </div>
    </div>

     Files List Section 
    <div class="col-12 col-lg-7">
      <div class="files-section">
        <h3 class="section-title">
          <span class="me-2">📚</span>
          Mes ressources ({{ files.length }})
        </h3>

        <div class="files-grid" *ngIf="files.length > 0">
          <div class="file-card" *ngFor="let f of files">
            <div class="file-header">
              <div class="file-info">
                <h4 class="file-title">{{ f.title }}</h4>
                <span class="file-type-badge">{{ getTypeLabel(f.type) }}</span>
              </div>
              <button 
                class="btn-delete" 
                (click)="delete(f.id)" 
                [disabled]="deletingId===f.id"
                title="Supprimer"
              >
                <span *ngIf="deletingId!==f.id">🗑️</span>
                <span *ngIf="deletingId===f.id" class="spinner-border spinner-border-sm"></span>
              </button>
            </div>

            <a class="btn btn-primary btn-sm w-100 mt-3" [href]="f.url" target="_blank">
              <span class="me-2">⬇️</span>
              Télécharger
            </a>
          </div>
        </div>

        <div class="empty-state" *ngIf="files.length === 0">
          <div class="empty-icon">📭</div>
          <p class="empty-text">Aucune ressource pour l'instant</p>
          <p class="empty-subtext">Commencez par ajouter votre première ressource</p>
        </div>
      </div>
    </div>
  </div>
</div>
