import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { ProfService } from '../../core/services/prof.service';

@Component({
  selector: 'app-student-home',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './student-home.component.html',
  styleUrls: ['./student-home.component.scss']
})
export class StudentHomeComponent implements OnInit {
  profs: any[] = [];
  loading = true;

  constructor(private profService: ProfService) {}

  ngOnInit() {
    this.profService.getAllProfs().subscribe({
      next: data => {
        this.profs = data;
        this.loading = false;
      },
      error: err => {
        console.error('Erreur chargement profs:', err);
        this.loading = false;
      }
    });
  }
}
<section class="student-home container py-5">
  <h1 class="mb-4 text-center fw-bold">Espace Étudiant</h1>
  <p class="text-center text-muted mb-5">
    Sélectionnez un professeur pour consulter ses cours, TD et TP.
  </p>

  <div *ngIf="loading" class="text-center my-5">
    <div class="spinner-border text-primary"></div>
  </div>

  <div class="row g-4" *ngIf="!loading">
    <div class="col-md-4" *ngFor="let prof of profs">
      <div class="card text-center shadow-sm h-100">
        <div class="card-body">
          <h4 class="fw-bold">{{ prof.name }}</h4>
          <p class="text-muted mb-3">{{ prof.email }}</p>
          <a [routerLink]="['/prof', prof.slug]" class="btn btn-primary">
            Voir les ressources
          </a>
        </div>
      </div>
    </div>

    <div *ngIf="!profs.length" class="alert alert-info text-center">
      Aucun professeur trouvé.
    </div>
  </div>
</section>
